class Toggler{toggleClass(elId,prCls,aftCls){let el=document.getElementById(elId);const isCCGO=el.className.split(" ").length>1;if (el.className.includes(prCls)&&!isCCGO){el.className=aftCls}else if(el.className.includes(prCls)&&isCCGO){let rplcd=el.className.replace(prCls,'');el.className=rplcd+aftCls;}else if(el.className.includes(aftCls)&&!isCCGO){el.className=prCls}else if(el.className.includes(aftCls)&&isCCGO){let rplcd=el.className.replace(aftCls,'');el.className=rplcd+prCls}}toggleImage(elId,frmImg,toImg){let el=document.getElementById(elId);if(el.src.includes(frmImg)){el.src=toImg}else if(el.src.includes(toImg)){el.src=frmImg}}toggleSlide(elId,dirc,spd){let el=document.getElementById(elId);let elWdt=el.offsetWidth;let elHgt=el.offsetHeight;let ofstlft=el.offsetLeft;let adps=ofstlft;let ofsttp=el.offsetTop;let addpstp=ofsttp;if(dirc=='horizontal'){if(ofstlft>-elWdt) tgl=setInterval(fdHri,spd);else if(ofstlft<=-elWdt) tgl=setInterval(fdOtHri,spd);}else if(dirc=='vertical'){if(ofsttp>-elHgt) tgl=setInterval(fdVrt,spd);else if(ofsttp<=-elHgt) tgl=setInterval(fdOtVrt,spd)}function fdHri(){if(ofstlft<=-elWdt) clearInterval(tgl);else{ofstlft-=10;el.style.left=ofstlft+'px'}}function fdOtHri(){if(ofstlft>=elWdt+adps) clearInterval(tgl);else{ofstlft+=10;el.style.left=ofstlft+'px'}}function fdVrt(){if(ofsttp<=-elHgt) clearInterval(tgl);else{ ofsttp-=10;el.style.top=ofsttp+'px'}}function fdOtVrt(){if(ofsttp>=elHgt+addpstp) clearInterval(tgl);else{ofsttp+=10;el.style.top=ofsttp+'px'}}}toggleProperty(elId,prprt,frmVal,toVal){let el=document.getElementById(elId);if(el.style.getPropertyValue(prprt)==null||el.style.getPropertyValue(prprt)==''){el.style.setProperty(prprt,toVal);return}if(el.style.getPropertyValue(prprt)==frmVal) el.style.setProperty(prprt,toVal);else el.style.setProperty(prprt,frmVal)}}